import React from "react"
import {Card, Button, CardColumns, Modal} from 'react-bootstrap'
import PostModal from "./PostModal"

export default function PostList(props) {
    const {posts, deletePost, showModal} = props
    const [modalOpen, setModalOpen] = useState(false)

    return(
        <div style={{paddingTop: 20}}>
            { modalOpen ? 
            (
                <PostModal/>
            )

            : (
            <CardColumns>
                {posts && posts.map(post => 
                <Card key={post.id} onClick={setModalOpen(true)}>
                    <Card.Body>
                        <Card.Title>{post.title.toUpperCase()}</Card.Title>
                        <Card.Text>{`${post.body.slice(0, 20)}...}`}</Card.Text>
                    </Card.Body>
                        <Card.Footer style={{testAlign: 'right', padding: 5}}>
                            <Button variant="danger" onClick={()=>deletePost(post.id)}>Delete</Button>
                        </Card.Footer>

                </Card>


                )}
            </CardColumns>
            )}
        </div>
    )
}
